import React, {Component, useState } from "react";
import "./header.scss";
import logo from "./img/logo.png";

function Header(props){

    const [showList, setShowList] = useState(false);

    const toggleList = () => {
      setShowList(!showList);
    };
  
  
        return(
            <div className="header">
                <img className="logo" src={logo} onClick={() => {props.changeCMP(0)}}/>
                <div className="modules">
                    <a href="#" onClick={() => {props.changeCMP(1)}}>Направления деятельности</a>
                    <a href="#" onClick={() => {props.changeCMP(3)}}>Услуги</a>
                    <a href="#" onClick={() => {props.changeCMP(2)}}>Продукты</a>
                    <a href="#" onClick={() => {props.changeCMP(5)}}>О нас</a>
                    <div className="toggle-row" onClick={toggleList}>
                    Click here to toggle the list
                    </div>
                        {showList && (
                        <div className="list">
                        <div className="list-item">Item 1</div>
                        <div className="list-item">Item 2</div>
                        <div className="list-item">Item 3</div>
                        </div>
                        )}
                </div>
                <button className="contact_us" onClick={() => {props.changeCMP(4)}}>Свяжитесь с нами</button>
            </div>
        )
    
}

export default Header
