import React, {Component, useState } from "react";
import "./header.scss";
import logo from "./img/logo.png";

class Header extends Component{
    constructor(props){
        super(props)
    }
    render(){
        const [showSelect, setShowSelect] = useState(false);
        return(
            <div className="header">
                <img className="logo" src={logo} onClick={() => {this.props.changeCMP(0)}}/>
                <div className="modules">
                    <a href="#" onClick={() => {this.props.changeCMP(1)}}>Направления деятельности</a>
                    <a href="#" onClick={() => {this.props.changeCMP(3)}}>Услуги</a>
                    <a href="#" onClick={() => {this.props.changeCMP(2)}}>Продукты</a>
                    <a href="#" onClick={() => {this.props.changeCMP(5)}}>О нас</a>
                    <div 
                    className="hover-area"
                    onMouseEnter={() => setShowSelect(true)}
                    onMouseLeave={() => setShowSelect(false)}
                    >
                    Hover over me
                        {showSelect && (
                        <select className="select-box">
                        <option value="1">Option 1</option>
                        <option value="2">Option 2</option>
                        <option value="3">Option 3</option>
                        </select>
                        )}
                    </div>

                </div>
                <button className="contact_us" onClick={() => {this.props.changeCMP(4)}}>Свяжитесь с нами</button>
            </div>
        )
    }
}

export default Header
