import React, {Component} from "react";
import "./App.css";
import Header from "./Header/Header";
import Main from "./Main/Main";
import FieldsOfActivity from "./FiledsOfActivity/FieldsOfActivity";
import Creations from "./Creations/Creations";

class App extends Component{
  constructor(props){
    super(props)

    this.CMPs = [
      <Main/>,
      <FieldsOfActivity/>,
      <Creations/>
    ]

    this.state = {
      cur_CMP: this.CMPs[0]
    }
  }

  changeCMP(value){
    this.setState(state => ({
      cur_CMP: this.CMPs[value]
    }))
  }

  render(){
    return(
      <div className="app">
        <Header/> 
        {this.state.cur_CMP}
      </div>
    )
  }
}

export default App
